function [mov_new]=red_mov(filename,new_filename,f)%converts an avi movie to a MATLAB movie file%then reduces the number of frames%returns the movie with fewer framesmov=aviread(filename);info=aviinfo(filename);new_frames=floor(info.NumFrames/f)-1;for i=1:new_frames    new_mov(i).cdata=mov(i*f).cdata;    new_mov(i).colormap=mov(i*f).colormap;endmovie2avi(new_mov,new_filename)