function F = find_rv(r,V,d)%takes in values for radius, volume, valve diameter%should return zero %F=pi/24*(16*r^3-(8*r^2+d^2)*(4*r^2-d^2)^(1/2))-V;%while F ~= 0%    if F > 0%        %    else            d=5;r=7:1000;E=.3749;for i=1:length(r)    V(i)=pi/24*(16*(r(i))^3-(8*(r(i))^2+d^2)*(4*(r(i))^2-d^2)^(1/2));    P(i)=4*E/d*(asin(d/(2*(r(i))))-d/(2*(r(i))));endplot(r,P)F=1;